INSERT INTO DisapprovalStandardComments (idDisapprovalStandardComments, name, displayPriority, inUse, description, added, updated) VALUES(1, 'Upper case', 1, 1, 'Do not use all capital letters for detail fields. The use of capital letters is considered shouting and seen by many to be poor ''netiquette''. Shouting is also considered very irritating to a lot of visitors and will reduce your response rate.', now(), now());
INSERT INTO DisapprovalStandardComments (idDisapprovalStandardComments, name, displayPriority, inUse, description, added, updated) VALUES(2, 'Email references', 2, 1, 'Do not reference email addresses within detail fields. Robots trawl websites and add email addresses to spamming lists making the email account holder a target for junkmail.', now(), now());
INSERT INTO DisapprovalStandardComments (idDisapprovalStandardComments, name, displayPriority, inUse, description, added, updated) VALUES(3, 'Website references', 3, 1, 'Links to external websites within detail fields are not permitted. You may reference your website once in the website field only.\r\n\r\nIndirect references such as ''see my website'' or ''click the link below'' are also not allowed. Fill fields with appropriate content within the context of the VillasForRent.NET website.', now(), now());
